(function(){

    function Application(delegate) {
        this.delegate = delegate;
        location.hash = "";
        window.onhashchange = this.onhashchange.bind(this);
    }

    Application.prototype.creationComplete = function() {
        var self = this;
        function IDelegate(){
            this.service = self.service.bind(self);
            this.requestConfirm = self.requestConfirm.bind(self);
            this.requestAlert = self.requestAlert.bind(self);
        }
        var delegate = new IDelegate();
        this.login = new view.components.Login(delegate);
        this.brand = new view.components.Brand(delegate);
        this.product = new view.components.Product(delegate);
        this.popup = new view.components.Popup();
    };

    Application.prototype.service = function(requestVO) {
        this.delegate.service(requestVO);
    };

    Application.prototype.requestConfirm = function(requestVO, message) {
        var self = this;
        this.popup.requestConfirm(requestVO, message)
            .then(function(requestVO){self.service(requestVO)});
    };

    Application.prototype.requestAlert = function(requestVO, message) {
        var self = this;
        this.popup.requestAlert(requestVO, message)
            .then(function(){});
    };

    Application.prototype.onhashchange = function(event) {
        this.login.onhashchange(event);
        this.brand.onhashchange(event);
        this.product.onhashchange(event);
    };

    Application.prototype.service_result = function(requestVO) {
        console.log(requestVO.getRequestType());
        switch (requestVO.getRequestType()) {
            case AppConstants.SIGN_IN_WITH_CREDENTIALS:
                this.login.signInWithCredentials_success(requestVO);
                break;
            case AppConstants.RENEW_AUTH_TOKEN:
                this.login.renewAuthToken_success(requestVO);
                break;
            case AppConstants.GET_BRANDS:
                this.brand.get_success(requestVO);
                this.product.setBrands(requestVO);
                break;
            case AppConstants.POST_BRANDS:
                this.brand.post_success(requestVO);
                this.product.appendBrand(requestVO.getResultData());
                break;
            case AppConstants.DELETE_BRANDS:
                this.brand.delete_success(requestVO);
                this.product.deleteBrand(requestVO.getRequestData());
                break;
            case AppConstants.GET_PRODUCTS:
                this.product.get_success(requestVO);
                break;
            case AppConstants.DELETE_PRODUCTS:
                this.product.delete_success(requestVO);
                break;
        }
    };

    Application.prototype.service_fault = function(requestVO) {
        console.error(requestVO.getRequestType());
        switch (requestVO.getRequestType()) {
            case AppConstants.SIGN_IN_WITH_CREDENTIALS:
                this.login.signInWithCredentials_fail(requestVO);
                break;
            case AppConstants.RENEW_AUTH_TOKEN:
                this.login.renewAuthToken_fail(requestVO);
                break;
            default:
                console.log(requestVO.getResultData());
                this.popup.requestAlert(requestVO, requestVO.getResultData())
                    .then(function(){});
                break;
        }
    };

    Application.prototype.setDelegate = function(delegate) {
        this.delegate = delegate;
    };

    view.components.Application = Application;

}());
(function(){

    function Brand(delegate) {
        this.delegate = delegate;
        document.getElementById("brandAdd").addEventListener("click", this.post.bind(this));
        this.list = document.getElementById("brandList");
        this.deleteList = document.getElementById("brandDeleteList");

        this.delegate.service(new model.vo.RequestVO(null, AppConstants.GET_BRANDS));
    }

    Brand.prototype.get_success = function(requestVO) {
        this.list.innerHTML = this.deleteList.innerHTML = "";
        var data = requestVO.getResultData();
        for(var i=0; i<data.length; i++) {
            this.addNode(data[i]);
        }
    };

    Brand.prototype.addNode = function(data) {
        var self = this;
        var li = document.createElement("li");
        li.setAttribute("id", "brand_" + data.id);
        li.innerHTML = data.name;
        this.list.appendChild(li);

        li = document.createElement("li");
        li.setAttribute("id", "brand_delete_" + data.id);
        li.innerHTML = "Delete";
        this.deleteList.appendChild(li);

        li.addEventListener("click", function(id){
            return function(event) {
                Brand.prototype.delete.call(self, id);
            };
        }(data.id));
    };

    Brand.prototype.post = function(event) {
        var data = document.getElementById("brand").value;
        if(data.trim() != "") {
            this.delegate.service(new model.vo.RequestVO(data, AppConstants.POST_BRANDS));
        }
    };

    Brand.prototype.post_success = function(requestVO) {
        document.getElementById("brand").value = "";
        var data = requestVO.getResultData();
        this.addNode(data);
    };

    Brand.prototype.delete = function(id) {
        this.delegate.requestConfirm(new model.vo.RequestVO(id, AppConstants.DELETE_BRANDS), "Are you sure you want to delete?");
    };

    Brand.prototype.delete_success = function(requestVO) {
        var element = document.getElementById("brand_" + requestVO.getRequestData());
        element.parentNode.removeChild(element);
        element = document.getElementById("brand_delete_" + requestVO.getRequestData( ));
        element.parentNode.removeChild(element);
    };

    Brand.prototype.onhashchange = function(event) {
        var hash = location.hash.slice(2);
        if(hash.indexOf("/brand") == -1) return;
        switch(hash) {
            case "/brands":
                break;
        }
    };

    view.components.Brand = Brand;

})();
(function(){

    function Login(delegate) {
        this.delegate = delegate;
        this.intervalId = null;
        this.username = document.getElementById("username");
        this.password = document.getElementById("password");
        this.loader = document.getElementById("loader");
        this.signIn = document.getElementById("signIn");
        document.getElementById("signIn").addEventListener("click", this.signInWithCredentials.bind(this));
        document.getElementById("signOut").addEventListener("click", this.signOut.bind(this));

        this.renewAuthToken();

        this.username.value = "admin@realogy.com";
        this.password.value = "admin101!";
    }

    Login.prototype.signOut = function() {
        clearInterval(this.intervalId);
        localStorage.removeItem("authToken");
        document.getElementById("login").classList.remove("hidden");
        document.getElementById("main").classList.add("hidden");
        document.getElementById("profile").classList.add("hidden");
    };

    Login.prototype.setInterval = function() {
        this.intervalId = setInterval(Login.prototype.renewAuthToken.bind(this), 5 * 60 * 1000);
    };

    Login.prototype.renewAuthToken = function() {
        if(localStorage.getItem("authToken")) {
            this.delegate.service(new model.vo.RequestVO(localStorage.getItem("authToken"), AppConstants.RENEW_AUTH_TOKEN));
        } else {
            this.renewAuthToken_fail();
        }
    };

    Login.prototype.renewAuthToken_success = function(requestVO) {
        this.signInWithCredentials_success(requestVO);
    };

    Login.prototype.renewAuthToken_fail = function(requestVO) {
        document.getElementById("login").classList.remove("hidden");
        document.getElementById("main").classList.add("hidden");
        localStorage.removeItem("authToken");
    };

    Login.prototype.signInWithCredentials = function(event) {
        if(this.username.value.trim() != "" && this.password.value.trim() != "") {
            this.enableFields(false);
            location.hash = "#!/login";
        }
    };

    Login.prototype.signInWithCredentials_success = function(requestVO) {
        this.enableFields(true);
        localStorage.setItem("authToken", requestVO.getResultData().getElementsByTagName("authToken")[0].firstChild.nodeValue);
        document.getElementById("login").classList.add("hidden");
        document.getElementById("main").classList.remove("hidden");

        document.getElementById("invalid").classList.add("hidden");
        location.hash = "#!/brands";

        this.setInterval();
    };

    Login.prototype.signInWithCredentials_fail = function(requestVO) {
        this.enableFields(true);
        document.getElementById("invalid").classList.remove("hidden");
    };

    Login.prototype.enableFields = function(enabled) {
        enabled ? this.username.removeAttribute("disabled") : this.username.setAttribute("disabled", "disabled");
        enabled ? this.password.removeAttribute("disabled") : this.password.setAttribute("disabled", "disabled");
        enabled ? this.signIn.removeAttribute("disabled") : this.signIn.setAttribute("disabled", "disabled");
        enabled ? this.loader.classList.add("hidden") : this.loader.classList.remove("invisible");
    };

    Login.prototype.onhashchange = function(event) {
        if(location.hash.indexOf("/login") == -1) return;
        switch(location.hash.slice(2)) {
            case "/login":
                this.delegate.service(new model.vo.RequestVO({username: this.username.value.trim(), password: this.password.value.trim()}, AppConstants.SIGN_IN_WITH_CREDENTIALS));
            break;
        }
    };

    view.components.Login = Login;

})();
$(document).ready(function() {

    $('.menu-btn').click( function() {
        if ( !$( this ).hasClass( "active" ) ) {
            $('.menu-btn').addClass('active');
        } else {
            $('.menu-btn').removeClass('active');
        }
        var toggleMenu = $(".nav").width() == 0 ? "100%" : "0px";
        var toggleContent = $("section").width() == 0 ? "100%" : "0";
        $('.nav').animate({ width: toggleMenu });
        $('section').animate({ width: toggleContent });
    });

    $('#user-btn').click( function(){
        $('.user-info').toggleClass('hidden');
    });

    $(".nav a").click(function(event) {
        event.preventDefault();
        location.hash = $(this).attr("data-hash");
        $(this).parent().addClass("active");
        $(this).parent().siblings().removeClass("active");
        var tab = $(this).attr("data-href");
        $(".content").not(tab).css("display", "none");
        $(tab).fadeIn();

        var mq = window.matchMedia( "(max-width: 480px)" );
        if (mq.matches) {
            $('.nav').css('width','0');
            $('section').css('width','100%');
        }
        if ( !$('.menu-btn').hasClass( "active" ) ) {
            $('.menu-btn').addClass('active');
        } else {
            $('.menu-btn').removeClass('active');
        }
    });
});

